import { makeStyles } from "@mui/styles";

export const useStyles = makeStyles({
  input: {
    color: "white",
    width: 390,
    backgroundColor: "transparent",
    border: "none",
    display: "flex",
    justifyContent: "center",
    paddingLeft: "7px",
  },

  important: {
    color: "#6a4f95",
  },
  done: {
    color: "#4d7a6a",
  },
  delete: {
    color: "#7d4c46",
  },
});
